import "./style.css";
import Campaign from "./campaign";
import Days from "./days";
import Condition from "./condition";
import Amount from "./amount";
function SingleBlock({ value, level }) {
  let shift = parseInt(level);
  let remainingSpan = parseInt(5) - parseInt(level) + 1;
  let gStyle = shift + "/span " + remainingSpan;
  return (
    <div className="rb-block" style={{ gridColumn: gStyle }}>
      {/* {value} */}
      <span style={{marginRight:10,position:"relative",top:"10px"}}>The </span><Campaign />
      <Days />
      <Condition />
      <Amount />   
       </div>
  );
}
export default SingleBlock;
